document.addEventListener("DOMContentLoaded",function(){const e=document.querySelectorAll(".section"),t=document.querySelector(".prev"),n=document.querySelector(".next"),o=document.querySelector(".scroll-to-top");let s=0;function c(){window.scrollTo({top:e[s].offsetTop,behavior:"smooth"})}t&&t.addEventListener("click",()=>{s>0&&(s--,c())}),n&&n.addEventListener("click",()=>{s<e.length-1&&(s++,c())}),window.addEventListener("scroll",()=>{window.pageYOffset>300?o.classList.add("show"):o.classList.remove("show")}),o.addEventListener("click",()=>{window.scrollTo({top:0,behavior:"smooth"})});const r=new IntersectionObserver(e=>{e.forEach(e=>{e.isIntersecting&&(s=Array.from(document.querySelectorAll(".section")).indexOf(e.target))})},{threshold:.5});e.forEach(e=>r.observe(e))}); 